package implement_strstr.java;

import java.nio.charset.StandardCharsets;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

class Solution {

    List<Byte> list = new ArrayList<>();

    public int strStr(String haystack, String needle) {
        if(needle.length() == 0){
            return 0;
        }
        System.out.println("start transfer bytes:" + System.currentTimeMillis());
        final byte[] bytes = needle.getBytes(StandardCharsets.UTF_8);
        System.out.println("stop transfer bytes:" + System.currentTimeMillis());
        final List<Map<Byte, Integer>> maps = computeDp(bytes);
        System.out.println("stop get map:" + System.currentTimeMillis());
        long start1 = System.currentTimeMillis();
        final int search = search(haystack, maps);
        long stop1 = System.currentTimeMillis();
        System.out.println("total:" + (stop1 - start1));


        for(Byte b : haystack.getBytes(StandardCharsets.UTF_8)){
            list.add(b);
        }
        System.out.println("start container:" + System.currentTimeMillis());
        long start2 = System.currentTimeMillis();
        contains(bytes);
        long stop2 = System.currentTimeMillis();
        System.out.println("total:" + (stop1 - start1));
        System.out.println("stop container:" + System.currentTimeMillis());

        return search;

    }

    public boolean contains(byte[] bytes) {
        for (int i = 0; i <= this.list.size() - bytes.length; i++) {
            if (bytes[0] == this.list.get(i)) {
                for (int j = 0; j < bytes.length; j++) {
                    if (bytes[j] != this.list.get(i + j)) {
                        break;
                    }
                    if (bytes[j] == this.list.get(i + j) && j == bytes.length - 1) {
                        // 最后一个字符相等表示包含此字节数组
                        return true;
                    }
                }

            }
        }
        return false;
    }

    public List<Map<Byte, Integer>> computeDp(byte[] bytes) {
        if(bytes.length == 0){
            return new ArrayList<>();
        }
        int x = 0;
        List<Map<Byte, Integer>> dp = new ArrayList<>();
        Map<Byte, Integer> firstMap = new HashMap<>();
        firstMap.put(bytes[0], 1);
        dp.add(firstMap);

        for (int j = 1; j < bytes.length; j++) {
            Map<Byte, Integer> nextMap = new HashMap<>();
            nextMap.put(bytes[j], j + 1);
            for (byte b : bytes) {
                if (dp.get(x).get(b) != null && !nextMap.containsKey(b)) {
                    nextMap.put(b, dp.get(x).get(b));
                }
            }
            dp.add(nextMap);
            x = dp.get(x).get(bytes[j]) == null ? 0 : dp.get(x).get(bytes[j]);
        }

        return dp;
    }

    public int search(String haystack, List<Map<Byte, Integer>> dp) {
        int j = 0;
        for (int i = 0; i < haystack.length(); i++) {
            j = dp.get(j).get((byte) haystack.charAt(i)) == null ? 0 : dp.get(j).get((byte) haystack.charAt(i));
            if (j == dp.size()) {
                return i - dp.size() + 1;
            }
        }
        return -1;
    }

    public static void case1(){
        System.out.println(new Solution().strStr("hello", "ll"));
    }

    public static void case2(){
        System.out.println(new Solution().strStr("", ""));
    }

    public static void case3(){
        System.out.println(new Solution().strStr("", "2323"));
    }

    public static void case4(){
        System.out.println(new Solution().strStr(""
               , "aaaaaaaaaaaab"));
    }

    public static void main(String[] args) {
        case1();
        System.out.println("=============================");
        case2();
        System.out.println("=============================");
        case3();
        System.out.println("=============================");
        case4();
    }

}

