package implement_strstr.java;

import java.nio.charset.StandardCharsets;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

class Solution {

    public int strStr(String haystack, String needle) {
        if (needle.length() == 0) {
            return 0;
        }
        final int[][] dp = computeDp(needle);
        return search(haystack, dp);
    }

    /**
     * kmp 算法计算dp的动态转移数组
     *
     * @param needle 匹配的字符串
     * @return dp动态转移数组
     */
    public int[][] computeDp(String needle) {
        int x = 0;
        int[][] dp = new int[needle.length()][123];
        dp[0][needle.charAt(0)] = 1;

        for (int j = 1; j < needle.length(); j++) {
            for (int c = 65; c <= 90; c++) {
                dp[j][c] = dp[x][c];
            }
            for (int c = 97; c <= 122; c++) {
                dp[j][c] = dp[x][c];
            }
            dp[j][needle.charAt(j)] = j + 1;
            x = dp[x][needle.charAt(j)];
        }
        return dp;
    }

    /**
     * 匹配字符串
     * 通过动态转移数组获取下一个状态
     *
     * @param haystack 待匹配字符串
     * @param dp 动态转移数组
     * @return 匹配结果
     */
    public int search(String haystack, int[][] dp) {
        int j = 0;
        for (int i = 0; i < haystack.length(); i++) {
            j = dp[j][haystack.charAt(i)];
            if (j == dp.length) {
                return i - dp.length + 1;
            }
        }
        return -1;
    }

    public static void case1() {
        System.out.println(new Solution().strStr("hello", "ll"));
    }

    public static void case2() {
        System.out.println(new Solution().strStr("", ""));
    }

    public static void case3() {
        System.out.println(new Solution().strStr("", "2323"));
    }

    public static void case4() {
        System.out.println(new Solution().strStr(
                ""
                ,
                ""));
    }

    public static void main(String[] args) {
        case1();
        System.out.println("=============================");
        case2();
        System.out.println("=============================");
        case3();
        System.out.println("=============================");
        case4();
    }

}

